import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { EmployeesService } from '../api/employees.service';

@Component({
  selector: 'app-add-employee',
  templateUrl: './add-employee.component.html',
  styleUrls: ['./add-employee.component.css']
})
export class AddEmployeeComponent implements OnInit {
  addEmployeeForm: FormGroup = new FormGroup({});
  employeeData: any;
  editEmployee = false;
  constructor(private fb: FormBuilder,private activatedRoute: ActivatedRoute,private employeesService: EmployeesService, private router: Router) { }

  ngOnInit(): void {
    this.initForm();
    this.activatedRoute.queryParams.subscribe(params => {
      if (params.employee!=null || params.employee!=undefined){
        console.log(params.employee)
        this.employeeData = JSON.parse(params.employee);
        this.editEmployee = true;
        this.setFormData();
      }

    })
  }
  initForm() {
    this.addEmployeeForm = this.fb.group({
      name: ['', [Validators.required]],
      departmentId: ['', [Validators.required]],

    })
  }
  setFormData(){
    this.addEmployeeForm.controls.name.patchValue(
      this.employeeData.name
    );
    this.addEmployeeForm.controls.departmentId.patchValue(
      this.employeeData.departmentId
    );

  }
  async addUser(){
    console.log(this.addEmployeeForm.value)
    let obj = {
      name: this.addEmployeeForm.value.name,
      departmentId : +this.addEmployeeForm.value.departmentId
    }
    if (this.editEmployee){

      await this.employeesService.editEmployee({id:this.employeeData.id, ...obj}).then((res:any)=>{
        {
          alert(res.message);
          this.router.navigateByUrl('employees/list');
      }
      })
    }
    else {
      await this.employeesService.addEmployee(obj).then((res:any)=>{
        {
          alert(res.message);
          this.router.navigateByUrl('employees/list');
      }
      })
    }
    }
  reload(){
    window.location.reload();
  }
}
<div style="margin: 50px;">
    <form [formGroup]="addEmployeeForm" (ngSubmit)="addUser()">

        <div class="row text-center">
          <div class="col-sm-12">
            <h3 class="wrapper__title">Employee Page</h3>
          </div>
          <div class="col-sm-12 col-md-12 col-lg-6">

              <label> Name: </label>
              <input formControlName="name" type="text">
              <small class="text-danger"  
                *ngIf=" addEmployeeForm.get('name')?.errors?.required &&  addEmployeeForm.get('name')?.touched"> Name Is
                Required
              </small>
          </div>
          <div class="col-sm-12 col-md-12 col-lg-6">

              <label>Department: </label>
              <select formControlName="departmentId" type="select">
                <option value="1">Finance</option>
               <option value="2">Marketing</option>
               <option value="3">IT</option>
               <option value="4">Engineering</option>
                </select>         <small class="text-danger"  
                      *ngIf=" addEmployeeForm.get('departmentId')?.errors?.required &&  addEmployeeForm.get('departmentId')?.touched"> Department Is Required
                    </small>
          </div>

          <div class="col-sm-12 mt-4" style="display: flex; justify-content: space-evenly;">
            <button  color="primary" class="btn btn-primary" type="submit" [disabled]=" addEmployeeForm.invalid"
               >Save</button>
               <button color="primary" class="btn btn-danger" type="button"
               >Cancel</button>
          </div>
        </div>
      </form>

</div>
